var gulp = require('gulp')
var config = require('../gulp-config')

gulp.task('lint', function() {
    var eslint = require('gulp-eslint')

    return gulp.src(config.paths.src.all.concat(config.paths.test.all))
        .pipe(eslint())
        .pipe(eslint.format())
        .pipe(eslint.failAfterError())
})
