var gulp = require('gulp')

gulp.task('server', function() {
    var browserSync = require('browser-sync')
    var reload = browserSync.reload

    browserSync({
        server: {
            baseDir: './dist'
        },
        ui: {
            port: 9001
        },
        open: 'local',
        startPath: '/sample',
        port: 9000,
        notify: false
    })

    gulp.watch(['./dist/**/*.*'], {cwd: './dist'}, reload)
})
