var config = require('../gulp-config')
var gulp = require('gulp')
var _ = require('lodash')

// Rerun the task when a file changes
gulp.task('watch', ['build-js:handleErrors', 'build-static'], function() {
    gulp.watch(config.paths.src.all, ['build-js:handleErrors'])
    _.each(config.paths.src.staticFiles, function(staticFile) {
        gulp.watch(staticFile.files, ['build-static'])
    })
})
