module.exports = {
    parseString: function(input, item) {
        var re = /\${([a-zA-z0-9]+?)}/
        do {
            var match = re.exec(input)
            if (match) {
                input = input.replace(match[0], item[match[1]] || '')
            }
        } while (match)
        return input
    },

    isValidGps: function(location) {
        return location &&
            location.lon >= -180.0 && location.lon <= 180.0 &&
            location.lat >= -90.0 && location.lat <= 90.0
    },

    createLonLat: function(lon, lat, map, ALKMaps) {
        return new ALKMaps.LonLat(lon, lat).transform(new ALKMaps.Projection('EPSG:4326'), map.getProjectionObject())
    }
}
