var VectorLayer = require('./VectorLayer')
var ImageLayerStyle = require('./styles/ImageLayerStyle')

module.exports = function(ALKMaps, map, options) {
    options.defaultStyle = options.defaultStyle || new ImageLayerStyle(ALKMaps, options)

    if (!options.selectedStyle) {
        options.selectedStyle = options.defaultStyle.clone()
        options.selectedStyle.defaultStyle.strokeColor = '#000000'
        options.selectedStyle.defaultStyle.fontSize = 14
    }

    options.layerName = options.layerName || 'Image Layer'

    return new VectorLayer(ALKMaps, map, options)
}
