var LayerApi = require('./LayerApi')

module.exports = function(ALKMaps, map, options) {

    var layer = new ALKMaps.Layer.Traffic(
        options.layerName || 'Traffic Layer',
        { region: options.region || 'NA' },
        { }
    )

    map.addLayer(layer)

    //API related stuff
    return new LayerApi(ALKMaps, map, layer, null, options)
}
