var LayerApi = require('./LayerApi')

module.exports = function(ALKMaps, map, options) {

    var layer = new ALKMaps.Layer.WeatherAlerts(
        options.layerName || 'Weather Alert Layer',
        { }
    )

    map.addLayer(layer)

    //API related stuff
    return new LayerApi(ALKMaps, map, layer, null, options)
}
