var LayerApi = require('./LayerApi')

module.exports = function(ALKMaps, map, options) {

    var layer = new ALKMaps.Layer.WeatherRadar(
        options.layerName || 'Weather Layer',
        { display: options.display || 'satellite' },
        { opacity: options.opacity || 0.5 }
    )

    map.addLayer(layer)

    //API related stuff
    return new LayerApi(ALKMaps, map, layer, null, options)
}
