angular
    .module('sampleAngularPnetMap', ['PnetMaps'])
    .controller('sampleController',
    function(pnetMap, ALKMaps) {
        pnetMap
            .init('CC87143E1602274A804BEF2AD8D24653', 'sampleMap')
            .addLayer('vehicle', 'No Action', { colorCode: '#1919FF', clusterPopupContent: '#${number} Trucks weeeeee!' })
            .plotPoints('No Action', vehicles5, { popupContent: '#${number}: Zoom Zoom!'})
            .addLayer('vehicle', 'Informational', { colorCode: '#CCCCCC', clusterPopupContent: '#${number} Trucks weeeeee!' })
            .plotPoints('Informational', vehicles4, { popupContent: '#${number}: Needs a wash.'})
            .addLayer('vehicle', 'Service Soon', { colorCode: '#FFFF00', clusterPopupContent: '#${number} Trucks weeeeee!' })
            .plotPoints('Service Soon', vehicles3, { popupContent: '#${number}: Someone scratched the door.'})
            .addLayer('vehicle', 'Service Now', { colorCode: '#FF9900', clusterPopupContent: '#${number} Trucks weeeeee!' })
            .plotPoints('Service Now', vehicles2, { popupContent: '#${number}: A wheel fell off.'})
            .addLayer('vehicle', 'Stop Now', { colorCode: '#CC0000', clusterPopupContent: '#${number} Trucks weeeeee!' })
            .plotPoints('Stop Now', vehicles1, { popupContent: '#${number}: It might blow up!'})
            .addLayer('marker', 'Dealers', { })
            .plotPoints('Dealers', landmarks1, { hoverField: 'name',
                icon: ALKMaps.IMAGE.TRUCK_RED })
            .plotPoints('Dealers', landmarks2, { hoverField: 'name',
                icon: ALKMaps.IMAGE.TRUCK_BLUE })
            .enableEvents()

        console.log(ALKMaps);
    })