require('../styles/applicationCard.scss')

module.exports = {
    template: require('../views/applicationCard.html'),
    bindings: {
        application: '<',
        currentUser: '<'
    },
    controller: class ApplicationCard {
        /* @ngInject */
        $onInit() {
            this.canEditApplication = this.currentUser.hasPermission('ROLE_APPLICATION_WRITE')
            this.canDeleteApplication = this.currentUser.hasPermission('ROLE_APPLICATION_DELETE')
        }
    }
}
