module.exports =
    /* @ngInject */
    function($mdIconProvider, customActionConfigProvider) {

        customActionConfigProvider.addTextButton('applicationList', 'default', {
            text: 'application.applicationEdit.add',
            click: (event, list) => {
                list.$state.go('nav.application.add')
            },
            permission: 'ROLE_APPLICATION_CREATE'
        })

        customActionConfigProvider.addTextButton('applicationList', 'alternate', {
            text: 'core.common.card.edit',
            click: (event, list, row) => {
                list.$state.go('nav.application.edit', row)
            },
            permission: 'ROLE_APPLICATION_WRITE'
        })


        customActionConfigProvider.addTextButton('applicationList', 'alternate', {
            text: 'core.common.card.delete',
            click: (event, list, row) => {
                let application = row

                return application.confirmDelete(event)
                    .then(() => {
                        if (application.deleted) {
                            list.listService.selectedRows = []
                        }
                    })
            },
            permission: 'ROLE_APPLICATION_DELETE'
        })
    }
