module.exports = {
    bindings: {
        value: '<',
        theme: '@'
    },
    controller: class ChipTheme {
        /* @ngInject */
        constructor($element) {
            this.$element = $element
        }

        $onInit() {
            this.applyTheme()
        }

        $onChanges() {
            this.applyTheme()
        }

        applyTheme() {
            let parent = this.$element.parent().parent().parent()

            if (this.value) {
                parent.addClass(this.theme)
            } else {
                parent.removeClass(this.theme)
            }
        }
    }
}
