require('../styles/mapDialog.scss')

module.exports = {
    template: require('../views/mapDialog.html'),
    clickOutsideToClose: true,
    escapeToClose: true,
    controllerAs: '$ctrl',
    controller: class MapDialog {
        /* @ngInject */
        constructor(defer) {
            this.defer = defer
        }

        mapLoaded(map) {
            this.defer.resolve(map)
        }
    }
}
