require('../styles/mapThumbnail.scss')

module.exports = {
    template: require('../views/mapThumbnail.html'),
    bindings: {
        height: '<',
        width: '<',
        location: '<',
        scale: '<'
    },
    controller: class MapThumbnail {
        /* @ngInject */
        constructor(mapUtilService) {
            this.mapUtilService = mapUtilService
        }

        $onInit() {
            this.mapUrl = this.mapUtilService.getSimpleMapUrl(this.location, this.scale)
        }

        showMapPopup(event) {
            return this.mapUtilService.showSimpleMapPopup(event, this.location)
        }
    }
}

