require('../styles/menu.scss')

module.exports = {
    template: require('../views/menuContainer.html'),
    controller: class MenuContainer {
        /* @ngInject */
        constructor(navigation) {
            this.navigation = navigation
        }

        $onInit() {
            this.sections = this.navigation.sections

            this.navigation.reset()
        }

        toggleOpen(section) {
            return this.navigation.toggleSelectSection(section)
        }

        isVisible(section) {
            return this.navigation.isVisible(section)
        }
    }
}
