module.exports = {
    bindings: {
        value: '<'
    },
    template: [
        '<div ng-show="$ctrl.value.streetAddress">{{$ctrl.value.streetAddress}}</div>',
        '<div ng-show="$ctrl.value.streetAddress2">{{$ctrl.value.streetAddress2}}</div>',
        '<div ng-show="$ctrl.value.city || $ctrl.value.state || $ctrl.value.zipcode">' +
            '{{$ctrl.value.city}}' +
            '{{$ctrl.value.city && $ctrl.value.state ? ", " : ""}}' +
            '{{$ctrl.value.state}}' +
            '{{($ctrl.value.city || $ctrl.value.state) && $ctrl.value.zipcode ? " " : ""}}' +
            '{{$ctrl.value.zipcode}}' +
        '</div>'
    ].join('')
}
