module.exports = /* @ngInject */
    function($mdThemingProvider) {

        var peoplenetBlue = $mdThemingProvider.extendPalette('light-blue', {
            500: '0194d3',
            600: '0194d3',
            contrastDefaultColor: 'light'
        })

        $mdThemingProvider.definePalette('peoplenetBlue', peoplenetBlue)

        var peoplenetOrange = $mdThemingProvider.extendPalette('orange', {
            700: 'ee7131',
            contrastDefaultColor: 'light',
            contrastDarkColors: ['100']
        })

        $mdThemingProvider.definePalette('peoplenetOrange', peoplenetOrange)

        $mdThemingProvider.theme('default')
            .primaryPalette('peoplenetBlue', {
                default: '500'
            })
            .accentPalette('peoplenetOrange', {
                default: '700',
                'hue-1': '700'
            })

        $mdThemingProvider.theme('warn')
            .primaryPalette('red')
            .accentPalette('grey')
    }
