module.exports = /* @ngInject */
    function(themeService) {
        return {
            restrict: 'A',
            link: function(scope, element) {
                let color = themeService.getColor()
                element.find('md-ink-bar')
                    .css('background-color', `rgb(${color[0]}, ${color[1]}, ${color[2]})`)
            }
        }
    }
