require('../styles/tabStyle.scss')

module.exports = /* @ngInject */
    function(themeService) {
        return {
            restrict: 'A',
            link: function(scope, element) {
                let color = themeService.getColor()
                element.addClass('tab-style')
                element.find('md-tabs-canvas').css('background-color', `rgb(${color[0]}, ${color[1]}, ${color[2]})`)
            }
        }
    }
