module.exports = /* @ngInject */
    function() {
        return {
            restrict: 'A',
            link: (s, el, attrs) => {
                let input = attrs.zHover || '1:6'
                let [, min, max] = input.match(/(\d+):(\d+)/)
                
                let over = `md-whiteframe-${max}dp`
                let out = `md-whiteframe-${min}dp`
                
                el.bind('mouseover', () => {
                    el.addClass(over)
                    el.removeClass(out)
                })

                el.bind('mouseout', () => {
                    el.addClass(out)
                    el.removeClass(over)
                })
            }
        }
    }
