module.exports = /* @ngInject */
    function($log) {
        return function(translationID) {
            if (/\d+$/.test(translationID)) {
                // no warnings for i18nService.getArray()
                return translationID
            }

            var matches = translationID.match(/.*\.(.*?)$/)
            if (matches) {
                $log.warn(`Translation for ${translationID} doesn't exist, falling back to ${matches[1]}.`)
                return matches[1]
            }
            $log.warn(`Translation for ${translationID} doesn't exist, using key.`)
            return translationID
        }
    }
