require('./listSelectionToolbar.scss')

module.exports = {
    bindings: {
        context: '<',
        currentUser: '<'
    },
    template: require('./listSelectionToolbar.html'),
    controller: class ListSelectionToolbar {
        /* @ngInject */
        constructor($state, $translate, customActionConfig, dialogService, listService) {
            this.$state = $state
            this.$translate = $translate
            this.customActionConfig = customActionConfig
            this.dialogService = dialogService
            this.listService = listService
        }

        $onInit() {
            this.alternateTextButtons = this.customActionConfig.getTextButtons(this.context, 'alternate')
            this.alternateIconButtons = this.customActionConfig.getIconButtons(this.context, 'alternate')
        }
    }
}
