require('./modalActionIcons.scss')
module.exports = {
    bindings: {
        row: '<',
        toggleActive: '&'
    },
    template: require('./modalActionIcons.html'),
    controller: class ModalActionIcons {
        toggleActiveItem(row) {
            if (!this.toggleActive(row)) {
                row.active = !row.active
            }
        }
    }
}
