var _ = require('lodash')

module.exports = /* @ngInject */
    function() {
        var menuItems = {
            items: [],
            themes: []
        }

        this.$get = () => {
            return _.cloneDeep(menuItems)
        }

        this.addLinkItem = item => {
            menuItems.items.push(item)
        }

        this.addThemeItem = theme => {
            menuItems.themes.push(theme)
        }
    }
