var _ = require('lodash')

/**
 * This provider allows us to access the private _PALETTES from $mdTheming.
 * This is otherwise normally inaccessible, but accessing it allows us to 
 * use the color values from themes in javascript instead of css.
 */
module.exports = /* @ngInject */
    function($mdThemingProvider) {
        this.$get = function() {
            return _.cloneDeep($mdThemingProvider._PALETTES)
        }
    }
