module.exports = class LoadingService {
    /* @ngInject */
    constructor() {
        this.isLoading = false
        this.isAsyncLoading = false
    }

    loading(value) {
        if (arguments.length > 0) {
            this.isLoading = !!value
        }

        return this.isLoading
    }

    asyncLoading(value) {
        if (arguments.length > 0) {
            this.isAsyncLoading = !!value
        }

        return this.isAsyncLoading
    }
}
