const _ = require('lodash')
require('../styles/toast.scss')

module.exports = class ToastService {
    /* @ngInject */
    constructor($mdToast) {
        this.$mdToast = $mdToast
    }

    show(message, options) {
        options = options || {}

        let toast = this.$mdToast
            .simple()
            .theme(options.theme || null)
            .content(message)
            .action(options.action || 'OK')
            .highlightAction(false)
            .hideDelay(_.isNumber(options.hideDelay) ? options.hideDelay : 5000)
            .position(options.position || 'bottom left right')

        return this.$mdToast.show(toast)
    }
}
