module.exports = class TokenService {
    /* @ngInject */
    constructor(localStorageService) {
        this.localStorageService = localStorageService
    }

    getEncodedToken() {
        return this.localStorageService.get('pnet.portal.encodedToken')
    }

    getToken() {
        return this.localStorageService.get('pnet.portal.idToken')
    }

    updateTokens(tokens) {
        this.localStorageService.set('pnet.portal.idToken', tokens.idToken)
        this.localStorageService.set('pnet.portal.encodedToken', tokens.encodedToken)
    }

    clear() {
        this.localStorageService.remove('pnet.portal.encodedToken')
        this.localStorageService.remove('pnet.portal.idToken')
    }
}
