let angular = require('angular')

angular.module('portal-core-login', ['portal-core'])

    //components
    .component('forgotPassword', require('./components/ForgotPassword'))
    .component('login', require('./components/Login'))
    .component('loginRoot', require('./components/LoginRoot'))
    .component('resetPassword', require('./components/ResetPassword'))
    .component('userEditSecurity', require('./components/UserEditSecurity'))

    //config
    .config(require('./config/loginRouteConfig'))

    //constants
    .constant('auth0Lock', require('./constants/fakeAuth0Lock'))

    //providers
    .provider('urlConfig', require('./providers/urlConfigProvider'))
        
    //run
    .run(require('./run/restangularConfig'))

    //services
    .service('authService', require('./services/AuthService'))
