var angular = require('angular')

angular.module('portal-core-login', ['portal-core'])

    //components
    .component('login', require('./components/Login'))
    .component('userEditSecurity', require('./components/UserEditSecurity'))

    //config
    .config(require('./config/loginRouteConfig'))
        
    //factories
    .factory('auth0Lock', require('./factories/auth0Lock'))

    //providers
    .provider('urlConfig', require('./providers/urlConfigProvider'))

    //run
    .run(require('./run/auth0Setup'))

    //services
    .service('authService', require('./services/AuthService'))
