require('./permissionDetails.scss')

module.exports = {
    template: require('./permissionDetails.html'),
    controller: class PermissionDetails {
        /* @ngInject */
        constructor($state) {
            this.$state = $state
        }

        $onInit() {
            this.currentUser = this.$state.$current.locals.globals.currentUser

            this.permission = this.$state.$current.locals.globals.permission

            this.canEditPermission = this.currentUser.hasPermission('ROLE_PERMISSION_WRITE')
        }
    }
}
