const angular = require('angular')

angular.module('portal-core-permission', ['portal-core'])

    //components
    .component('permissionCard', require('./card/PermissionCard'))
    .component('permissionDetails', require('./details/PermissionDetails'))
    .component('permissionEdit', require('./edit/PermissionEdit'))
    .component('permissionList', require('./list/PermissionList'))

    //config
    .config(require('./config/permissionCustomActionConfig'))
    .config(require('./config/permissionGlobalSearchConfig'))
    .config(require('./config/permissionNavigationConfig'))
    .config(require('./config/permissionRouteConfig'))

    //run
    .run(require('./run/permissionRestangularConfig'))
