require('../styles/roleCard.scss')

module.exports = {
    template: require('../views/roleCard.html'),
    bindings: {
        role: '<',
        currentUser: '<'
    },
    controller: class RoleCard {
        /* @ngInject */
        constructor($mdMedia) {
            this.$mdMedia = $mdMedia
        }

        $onInit() {
            this.canDeleteRoles = this.currentUser.hasPermission('ROLE_ROLE_DELETE')
            this.canWriteRoles = this.currentUser.hasPermission('ROLE_ROLE_WRITE')
        }
    }
}
