module.exports =
    /* @ngInject */
    function($mdIconProvider, customActionConfigProvider) {

        customActionConfigProvider.addTextButton('roleList', 'default', {
            text: 'role.roleList.add_role',
            click: (event, list) => {
                list.$state.go('nav.role.add')
            },
            permission: 'ROLE_ROLE_CREATE'
        })

        customActionConfigProvider.addTextButton('roleList', 'alternate', {
            text: 'core.common.card.edit',
            click: (event, list, row) => {
                list.$state.go('nav.role.edit', row)
            },
            permission: 'ROLE_ROLE_WRITE'
        })


        customActionConfigProvider.addTextButton('roleList', 'alternate', {
            text: 'core.common.card.delete',
            click: (event, list, row) => {
                let role = row

                return role.confirmDelete(event)
                    .then(() => {
                        if (role.deleted) {
                            list.listService.selectedRows = []
                        }
                    })
            },
            permission: 'ROLE_ROLE_DELETE'
        })
    }
