const _ = require('lodash')

require('./roleListDialog.scss')

module.exports = {
    template: require('./roleListDialog.html'),
    clickOutsideToClose: true,
    escapeToClose: true,
    controllerAs: '$ctrl',
    controller: class RoleListDialog {
        /* @ngInject */
        constructor($filter, $mdDialog, $translate, assignee, listService, roles) {
            this.$filter = $filter
            this.$mdDialog = $mdDialog
            this.$translate = $translate
            this.assignee = assignee
            this.listService = listService
            this.roles = roles

            this.init = this.onInit()
        }

        onInit() {
            this.context = 'roleListDialog'

            this.toolbarTitle = 'roleNew.roleList.title'

            this.cols = [
                {
                    name: 'roleNew.roleList.cols.name',
                    path: 'name',
                    sortable: true
                },
                {
                    name: 'roleNew.roleList.cols.actions',
                    template: '<modal-action-icons row="row"></modal-action-icons>'
                }
            ]

            this.staticResults = _.map(this.roles, role => ({
                icon: 'person',
                name: role.roleName,
                type: 'static',
                key: role.roleId,
                term: role.roleName,
                param: 'role'
            }))

            this.rows = this.roles

            this.total = _.size(this.rows)

            this.toolbarOptions = {
                filterButton: true
            }

            this.tableOptions = {
                rowSelect: true
            }

            this.paginationOptions = {
                boundaryLinks: true,
                pageSizes: [10, 25, 50]
            }

            this.chipFilterOptions = {
                staticResults: this.staticResults
            }

            this.listService.page = 1
            this.listService.pageSize = 10

            this.params = {
                page: this.listService.page - 1,
                pageSize: this.listService.pageSize
            }

            this.getRows()

            return this.loadTranslations()
        }

        loadTranslations() {
            let translations = [
                'roleNew.roleListDialog.subheader',
                'roleNew.roleListDialog.instructions'
            ]

            return this.$translate(translations)
                .then(res => this.res = res)
        }

        getRows() {
            let start = this.params.page * this.params.pageSize
            let end = start + this.params.pageSize
            this.rows = _.slice(this.roles, start, end)
        }

        changePage(params) {
            this.params.page = params.page
            this.params.pageSize = params.pageSize

            this.listService.page = params.page + 1
            this.listService.pageSize = params.pageSize

            return this.getRows()
        }

        checkChipsCustom() {
            return !!this.chips
        }

        clearChipsCustom() {
            this.getRows()
            return _.remove(this.chips)
        }

        chipsUpdated(selectedChips) {
            if (_.get(selectedChips.selectedChips, 'length')) {
                let results = _.reduce(selectedChips.selectedChips, (result, chip) => {
                    let found = this.$filter('filter')(this.roles, chip.term)
                    result.push(found)
                    return result
                }, [])

                //find the results that match all chips and put them first in the results
                //then put the rest of the results in the list - this should relatively
                //simulate how elastic search returns search results
                this.rows = _.uniq(_.intersection(...results).concat(_.flatten(results)))

                this.total = _.size(this.rows)
            } else {
                this.getRows()

                this.total = _.size(this.roles)
            }

            //stop the state reload from happening
            return true
        }

        cancel() {
            this.$mdDialog.cancel()
        }

        save() {
            let roles = _.filter(this.roles, {active: true})
            this.$mdDialog.hide(roles)
        }
    }
}
