require('../styles/userDetails.scss')

module.exports = {
    template: require('../views/userDetailsProfile.html'),
    controller: class UserDetailsProfile {
        /* @ngInject */
        constructor($state) {
            this.$state = $state
        }

        $onInit() {
            //TODO: replace this workaround when new router is available
            this.user = this.$state.$current.locals.globals.user
            this.currentUser = this.$state.$current.locals.globals.currentUser
            this.currentOrg = this.$state.$current.locals.globals.currentOrg

            this.canEditUser = this.currentUser.canEditUser(this.user)
        }
    }
}
