var _ = require('lodash')

module.exports = /* @ngInject */
    function() {
        var userTabs = {
            detailsTabs: [],
            editTabs: []
        }

        this.$get = () => {
            return _.cloneDeep(userTabs)
        }

        this.addDetailsTab = (label, template) => {
            userTabs.detailsTabs.push({
                label, template
            })
        }

        this.addEditTab = (label, template) => {
            userTabs.editTabs.push({
                label, template
            })
        }
    }
