const angular = require('angular')

angular.module('portal-core-user', ['portal-core'])

    //components
    .component('userCard', require('./components/UserCard'))
    .component('userDetailsContainer', require('./components/UserDetailsContainer'))
    .component('userDetailsProfile', require('./components/UserDetailsProfile'))
    .component('userEditContainer', require('./components/UserEditContainer'))
    .component('userEditProfile', require('./components/UserEditProfile'))
    .component('userEditTags', require('./components/UserEditTags'))
    .component('userList', require('./components/UserList'))

    //config
    .config(require('./config/userCustomActionConfig'))
