module.exports =
    /* @ngInject */
    function($mdIconProvider, customActionConfigProvider) {

        customActionConfigProvider.addTextButton('userList', 'default', {
            text: 'user.userList.add_user',
            click: (event, list) => {
                list.$state.go('nav.user.add')
            }
        })

        customActionConfigProvider.addTextButton('userList', 'alternate', {
            text: 'core.common.card.edit',
            click: (event, list, row) => {
                list.$state.go('nav.user.edit', row)
            }
        })

        customActionConfigProvider.addTextButton('userList', 'alternate', {
            text: 'core.common.card.details',
            click: (event, list, row) => {
                list.$state.go('nav.user.details', row)
            }
        })

        customActionConfigProvider.addTextButton('userList', 'alternate', {
            text: 'core.common.card.delete',
            click: (event, list, row) => {
                let user = row

                return user.confirmDelete(event)
                    .then(() => {
                        if (user.deleted) {
                            list.listService.selectedRows = []
                        }
                    })
            }
        })
    }

