require('./userEditContainer.scss')

module.exports = {
    template: require('./userEditContainer.html'),
    controller: class UserEditContainer {
        /* @ngInject */
        constructor($state, userTabConfig) {
            this.$state = $state
            this.userTabConfig = userTabConfig
        }

        $onInit() {
            //TODO: replace this workaround when new router is available
            this.currentUser = this.$state.$current.locals.globals.currentUser

            this.tabs = this.userTabConfig.editTabs
            this.isAdmin = this.currentUser.orgType.toLowerCase() === 'administrative'
        }
    }
}
