describe('ApplicationCard', function() {
    var currentUser
    var component
    var ctrl

    beforeEach(function() {
        currentUser = {}
        currentUser.hasPermission = sinon.stub().returnsArg(0)

        component = require('../../../main/application/components/ApplicationCard')
        ctrl = new component.controller()
        ctrl.currentUser = currentUser
    })

    it('should expose some auth variables', function() {
        ctrl.$onInit()

        ctrl.canEditApplication.should.equal('ROLE_APPLICATION_WRITE')
        ctrl.canDeleteApplication.should.equal('ROLE_APPLICATION_DELETE')
    })
})
