describe('applicationNavigationConfig', function() {
    var navigationProvider

    beforeEach(function() {
        navigationProvider = {}
        navigationProvider.addMenuItem = sinon.stub()

        require('../../../main/application/config/applicationNavigationConfig')(navigationProvider)
    })

    it('should register for navigation menu', function() {
        navigationProvider.addMenuItem.should.have.been.calledWith('link', 'application.common.menu')
    })

})
