describe('menuContainer', () => {
    let component
    let ctrl
    let navigation

    beforeEach(() => {
        navigation = {}
        navigation.sections = ['1', '2', '3']
        navigation.reset = sinon.stub()
        navigation.toggleSelectSection = sinon.stub()
        navigation.isVisible = sinon.stub()

        component = require('../../../main/core/components/MenuContainer')
        ctrl = new component.controller(navigation)
        ctrl.$onInit()
    })

    it('should initialize', () => {
        ctrl.$onInit()
        ctrl.sections.should.equal(navigation.sections)
        navigation.reset.should.have.been.called
    })

    it('should toggle open', () => {
        ctrl.toggleOpen(navigation.sections)

        navigation.toggleSelectSection.should.have.been.calledWith(navigation.sections)
    })

    it('should return if section is visible', () => {
        ctrl.isVisible(navigation.sections)

        navigation.isVisible.should.have.been.calledWith(navigation.sections)
    })
})

