describe('coreConfig', function() {
    var $logProvider
    var $urlRouterProvider

    beforeEach(function() {
        $logProvider = {}
        $logProvider.debugEnabled = sinon.stub()

        $urlRouterProvider = {}
        $urlRouterProvider.otherwise = sinon.stub()

        require('../../../main/core/config/coreConfig')($logProvider, $urlRouterProvider)
    })

    it('should disable debug logging', function() {
        $logProvider.debugEnabled.should.have.been.calledWith(false)
    })

    it('should set the default route', function() {
        $urlRouterProvider.otherwise.should.have.been.calledWith('/nav/welcome')
    })
})
