describe('autoFocusDirective', function() {
    var autoFocusDirective

    beforeEach(function() {
        autoFocusDirective = require('../../../main/core/directives/autoFocusDirective')()
    })

    it('should be restricted to attribute usage', function() {
        autoFocusDirective.restrict.should.equal('A')
    })

    it('should set focus to first element', function() {
        var element = [
            {
                focus: sinon.stub()
            }
        ]

        autoFocusDirective.link.post(null, element)

        element[0].focus.should.have.been.called
    })
})
