describe('tabStyleDirective', () => {
    let tabStyleDirective
    let themeService

    beforeEach(() => {
        themeService = {}
        
        tabStyleDirective = require('../../../main/core/directives/tabStyleDirective')(themeService)
    })

    it('should be restricted to attribute usage', () => {
        tabStyleDirective.restrict.should.equal('A')
    })

    it('should apply styles', () => {
        themeService.getColor = sinon.stub().returns([11, 22, 33])
        let found = {
            css: sinon.stub()
        }
        let element = {
            addClass: sinon.stub(),
            find: sinon.stub().returns(found)
        }

        tabStyleDirective.link(null, element, {})

        element.addClass.should.have.been.calledWith('tab-style')
        found.css.should.have.been.calledWith('background-color', 'rgb(11, 22, 33)')
    })
})
