describe('encodeURIComponentFilter', function() {
    var $window
    var encodeURIComponentFilter

    beforeEach(function() {
        $window = {}
        $window.encodeURIComponent = sinon.stub()

        encodeURIComponentFilter = require('../../../main/core/filters/encodeURIComponentFilter')($window)
    })

    it('has an encodeURIComponentFilter', function() {
        encodeURIComponentFilter.should.be.defined
    })

    it('calls the wrapped function', function() {
        var uri = 'http://hello.com'

        encodeURIComponentFilter(uri)

        $window.encodeURIComponent.should.have.been.calledWithExactly(uri)
    })
})
