describe('phoneNumberFilter', function() {
    var phoneNumberFilter

    beforeEach(function() {
        phoneNumberFilter = require('../../../main/core/filters/phoneNumberFilter')()
    })

    it('has a phone number filter', function() {
        phoneNumberFilter.should.be.defined
    })

    it('should return a formatted phone number ', function() {
        phoneNumberFilter('1234567890').should.equal('(123) 456-7890')
        phoneNumberFilter('123-456-7890').should.equal('(123) 456-7890')
        phoneNumberFilter('(123)456-7890').should.equal('(123) 456-7890')
    })

    it('should return its input', function() {
        phoneNumberFilter('asdf_qwerty').should.equal('asdf_qwerty')
        phoneNumberFilter('123456789123456').should.equal('123456789123456')
        phoneNumberFilter('121234567890').should.equal('121234567890')
        phoneNumberFilter('+121234567890').should.equal('+121234567890')
        phoneNumberFilter('11234567890').should.equal('11234567890')
        phoneNumberFilter('+1.800.123.1234').should.equal('+1.800.123.1234')
        phoneNumberFilter('+1 -800 - 123 - 1234').should.equal('+1 -800 - 123 - 1234')
        phoneNumberFilter('').should.equal('')
    })

    it('should return an empty string', function() {
        phoneNumberFilter('').should.equal('')
        phoneNumberFilter().should.equal('')
        phoneNumberFilter(undefined).should.equal('')
        phoneNumberFilter(null).should.equal('')
        phoneNumberFilter({}).should.equal('')
        phoneNumberFilter([]).should.equal('')
    })
})
