describe('startCaseFilter', function() {
    var startCaseFilter

    beforeEach(function() {
        startCaseFilter = require('../../../main/core/filters/startCaseFilter')()
    })

    it('should format reccomendations', function() {
        startCaseFilter('stop_now').should.equal('Stop Now')
    })

    it('should trim strings', function() {
        startCaseFilter('').should.equal('')
        startCaseFilter('  ').should.equal('')
        startCaseFilter('asdf   ').should.equal('Asdf')
        startCaseFilter('Asdf   ').should.equal('Asdf')
        startCaseFilter('Asdf   \n \r \t').should.equal('Asdf')
    })

    it('should remove special chars from strings', function() {
        startCaseFilter('  !@#$%^&*()  ').should.equal('')
        startCaseFilter('asdf!@#$%^&*()').should.equal('Asdf')
        startCaseFilter('asdf_asdf').should.equal('Asdf Asdf')
    })

    it('should returns its input when not given a string ', function() {
        startCaseFilter().should.equal('')
        startCaseFilter(undefined).should.equal('')
        startCaseFilter(null).should.equal('')
        startCaseFilter({}).should.deep.equal('')
        startCaseFilter([]).should.deep.equal('')
    })
})
