describe('ListPagination', () => {
    let $state
    let $stateParams
    let ListPagination
    let ctrl

    beforeEach(() => {
        $state = {
            current: '',
            go: sinon.stub()
        }

        $stateParams = {

        }

        ListPagination = require('../../../../main/core/list/pagination/ListPagination')

        ctrl = new ListPagination.controller($state, $stateParams)

        ctrl.$onInit()
    })

    it('should handle pagination change', () => {
        let params = {
            page: 0,
            pageSize: 10
        }

        ctrl.changePage = sinon.stub()

        ctrl.onPageChange(1, 10)

        ctrl.changePage.should.have.been.calledWith(params)
    })
})
